/*
 * To display social media links at any position in a page, call the socialMediaLinks() function
 * passing in the required social media title and elements.
 *
 * Valid options will be inserted at the point in the page where the function call is made.
 * Invalid options will be ignored.
 *
 * E.g.
 * This function call:
 * <script language="JavaScript">
 *     socialMediaLinks("Share",["facebook", "twitter"]);
 * </script>
 *
 * Results in the following HTML being inserted at the calling location in the page:
 * <h2>Share</h2>
 * <ul class="social"><li><a class="btn btn-social"
 * href="https://www.facebook.com/sharer/sharer.php?u=http%3A%2F%2Flocalhost%2Fuoe%2Fedgel_social_media%2Fexample-page-1.html"
 * title="Share this content on Facebook." role="button" target="_blank" aria-label="Share this content on Facebook.">
 * <svg>...</svg></a></li>
 * <li><a class="btn btn-social"
 * href="https://twitter.com/share?url=http%3A%2F%2Flocalhost%2Fuoe%2Fedgel_social_media%2Fexample-page-1.html&amp;text=Social%20Media%20Demo%20-%20Image"
 * title="Share this content on Twitter." role="button" target="_blank" aria-label="Share this content on Twitter.">
 * <svg>...</svg></a></li></ul>
 *
 */

EdGel.socialMedia.links = function (linksTitle, linksItems) {
  'use strict';

  if (document.head.querySelector('meta[property="og:title"]')) {
    var args = Array.isArray(linksItems) ? linksItems : [linksItems];
    var liItems = '';
    var ref = {};

    for (var idxArg = 0; idxArg < args.length; idxArg++) {
      // Add valid social media link items to the list.
      if (args[idxArg] in EdGel.socialMedia.buttons) {
        ref = EdGel.socialMedia.buttons[args[idxArg]];
        liItems += '<li>' + ref.buttonLink + ref.buttonSVG + '</a></li>';
      }
    }

    if (liItems) {
      // Insert the list.
      document.writeln('<h2>' + linksTitle + '</h2><ul class="social">' + liItems + '</ul>'); // jshint ignore:line
    }
  }
  else {
    throw new Error('There are no open graph meta tags present. Please add them to your page. Social Media Links have not been inserted into this page.')
  }

};

